document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger,SplitText);const defaults_fade={duration:.5,delay:0,stagger:.1},defaults_slideUp={duration:.3,delay:0,stagger:.1,distance:"50px"},defaults_splitText={duration:.5,delay:0,stagger:.15};function initAnimations(){document.querySelectorAll("[fade-in]").forEach(element=>{const duration=element.dataset.duration||defaults_fade.duration,delay=element.dataset.delay||defaults_fade.delay,stagger=element.dataset.stagger||defaults_fade.stagger;gsap.from(element,{opacity:0,duration:parseFloat(duration),delay:parseFloat(delay),stagger:parseFloat(stagger),scrollTrigger:{trigger:element,start:"top 75%",toggleActions:"play none none pause"}})}),document.querySelectorAll("[slide-up]").forEach(element=>{const duration=element.dataset.duration||defaults_slideUp.duration,delay=element.dataset.delay||defaults_slideUp.delay,stagger=element.dataset.stagger||defaults_slideUp.stagger,distance=element.dataset.distance||defaults_slideUp.distance;gsap.from(element,{opacity:0,y:distance,duration:parseFloat(duration),delay:parseFloat(delay),stagger:parseFloat(stagger),scrollTrigger:{trigger:element,start:"top 75%",toggleActions:"play none none pause"}})}),document.querySelectorAll("[split-text]").forEach(element=>{let splitType="lines";element.hasAttribute("split-char")?splitType="chars":element.hasAttribute("split-word")&&(splitType="words");const duration=element.dataset.duration||defaults_splitText.duration,delay=element.dataset.delay||defaults_splitText.delay,stagger=element.dataset.stagger||defaults_splitText.stagger,split=new SplitText(element,{type:splitType});gsap.from(split[splitType],{opacity:0,y:"100%",duration:parseFloat(duration),delay:parseFloat(delay),stagger:parseFloat(stagger),scrollTrigger:{trigger:element,start:"top 85%",markers:!0,toggleActions:"play none none pause"}})})}initAnimations(),window.addEventListener("resize",()=>{ScrollTrigger.refresh()})});