class GSAPAnimations{constructor(){this.init()}init(){gsap.registerPlugin(ScrollTrigger,SplitText),this.initFadeIn(),this.initSlideUp(),this.initSplitText()}getDataAttributes(element,defaults){const attrs={};for(const[key,defaultValue]of Object.entries(defaults)){const value=element.dataset[key];attrs[key]=void 0!==value?value:defaultValue}return attrs}initFadeIn(){const elements=document.querySelectorAll('[data-animation="fade"]');elements.forEach(element=>{const{stagger:stagger=.1,duration:duration=.3,delay:delay=0}=this.getDataAttributes(element,{stagger:.1,duration:.3,delay:0});gsap.from(element,{opacity:0,duration:parseFloat(duration),delay:parseFloat(delay),stagger:parseFloat(stagger),scrollTrigger:{trigger:element,start:"top 75%",toggleActions:"play none none reverse"}})})}initSlideUp(){const elements=document.querySelectorAll('[data-animation="slide-up"]');elements.forEach(element=>{const{stagger:stagger=.1,duration:duration=.3,delay:delay=0,distance:distance="80%"}=this.getDataAttributes(element,{stagger:.1,duration:.3,delay:0,distance:"80%"});gsap.from(element,{y:distance,opacity:0,duration:parseFloat(duration),delay:parseFloat(delay),stagger:parseFloat(stagger),scrollTrigger:{trigger:element,start:"top 75%",toggleActions:"play none none reverse"}})})}initSplitText(){const elements=document.querySelectorAll('[data-animation="split-text"]');elements.forEach(element=>{const{split:split="words",stagger:stagger=.15,duration:duration=.5,delay:delay=0}=this.getDataAttributes(element,{split:"words",stagger:.15,duration:.5,delay:0}),splitText=new SplitText(element,{type:split});gsap.from(splitText[split],{y:"100%",opacity:0,duration:parseFloat(duration),delay:parseFloat(delay),stagger:parseFloat(stagger),scrollTrigger:{trigger:element,start:"top 75%",toggleActions:"play none none reverse"}})})}}document.addEventListener("DOMContentLoaded",()=>{new GSAPAnimations});